import 'package:flutter/material.dart';\nimport '../models/product.dart';\nimport '../widgets/product_card.dart';\n\nclass HomeScreen extends StatelessWidget {\n  const HomeScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      backgroundColor: const Color(0xFFf5f5f5),\n      body: CustomScrollView(\n        slivers: [\n          SliverAppBar(\n            expandedHeight: 200,\n            floating: false,\n            pinned: true,\n            backgroundColor: const Color(0xFF1a1a2e),\n            flexibleSpace: FlexibleSpaceBar(\n              title: Row(\n                mainAxisSize: MainAxisSize.min,\n                children: [\n                  const Icon(Icons.wb_twilight, color: Color(0xFFe94560)),\n                  const SizedBox(width: 8),\n                  const Text(\n                    'DRAGON',\n                    style: TextStyle(\n                      fontWeight: FontWeight.bold,\n                      letterSpacing: 2,\n                    ),\n                  ),\n                ],\n              ),\n              background: Container(\n                decoration: BoxDecoration(\n                  gradient: LinearGradient(\n                    begin: Alignment.topCenter,\n                    end: Alignment.bottomCenter,\n                    colors: [\n                      const Color(0xFF1a1a2e),\n                      const Color(0xFF0f3460),\n                    ],\n                  ),\n                ),\n                child: Center(\n                  child: Column(\n                    mainAxisAlignment: MainAxisAlignment.center,\n                    children: [\n                      const Icon(\n                        Icons.wb_twilight,\n                        size: 60,\n                        color: Color(0xFFe94560),\n                      ),\n                      const SizedBox(height: 10),\n                      Text(\n                        'Premium Products Store',\n                        style: TextStyle(\n                          color: Colors.white.withOpacity(0.8),\n                          fontSize: 16,\n                        ),\n                      ),\n                    ],\n                  ),\n                ),\n              ),\n            ),\n          ),\n          SliverToBoxAdapter(\n            child: Padding(\n              padding: const EdgeInsets.all(16.0),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  // Search Bar\n                  Container(\n                    decoration: BoxDecoration(\n                      color: Colors.white,\n                      borderRadius: BorderRadius.circular(15),\n                      boxShadow: [\n                        BoxShadow(\n                          color: Colors.black.withOpacity(0.05),\n                          blurRadius: 10,\n                          offset: const Offset(0, 5),\n                        ),\n                      ],\n                    ),\n                    child: TextField(\n                      decoration: InputDecoration(\n                        hintText: 'Search products...',\n                        prefixIcon: const Icon(Icons.search, color: Color(0xFF0f3460)),\n                        border: OutlineInputBorder(\n                          borderRadius: BorderRadius.circular(15),\n                          borderSide: BorderSide.none,\n                        ),\n                        filled: true,\n                        fillColor: Colors.white,\n                      ),\n                    ),\n                  ),\n                  const SizedBox(height: 24),\n                  \n                  // Categories\n                  const Text(\n                    'Categories',\n                    style: TextStyle(\n                      fontSize: 22,\n                      fontWeight: FontWeight.bold,\n                      color: Color(0xFF1a1a2e),\n                    ),\n                  ),\n                  const SizedBox(height: 12),\n                  SizedBox(\n                    height: 100,\n                    child: ListView(\n                      scrollDirection: Axis.horizontal,\n                      children: [\n                        _buildCategoryChip('Electronics', Icons.phone_android),\n                        _buildCategoryChip('Fashion', Icons.checkroom),\n                        _buildCategoryChip('Home', Icons.home),\n                        _buildCategoryChip('Beauty', Icons.face),\n                        _buildCategoryChip('Sports', Icons.sports_soccer),\n                      ],\n                    ),\n                  ),\n                  const SizedBox(height: 24),\n                  \n                  // Featured Products\n                  Row(\n                    mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                    children: [\n                      const Text(\n                        'Best Selling Products',\n                        style: TextStyle(\n                          fontSize: 22,\n                          fontWeight: FontWeight.bold,\n                          color: Color(0xFF1a1a2e),\n                        ),\n                      ),\n                      TextButton(\n                        onPressed: () {},\n                        child: const Text(\n                          'View All',\n                          style: TextStyle(color: Color(0xFFe94560)),\n                        ),\n                      ),\n                    ],\n                  ),\n                  const SizedBox(height: 12),\n                ],\n              ),\n            ),\n          ),\n          SliverPadding(\n            padding: const EdgeInsets.symmetric(horizontal: 16),\n            sliver: SliverGrid(\n              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(\n                crossAxisCount: 2,\n                childAspectRatio: 0.7,\n                crossAxisSpacing: 12,\n                mainAxisSpacing: 12,\n              ),\n              delegate: SliverChildBuilderDelegate(\n                (context, index) {\n                  final product = Product.getBestSellers()[index];\n                  return ProductCard(product: product);\n                },\n                childCount: Product.getBestSellers().length,\n              ),\n            ),\n          ),\n          const SliverToBoxAdapter(child: SizedBox(height: 20)),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildCategoryChip(String label, IconData icon) {\n    return Container(\n      margin: const EdgeInsets.only(right: 12),\n      width: 100,\n      decoration: BoxDecoration(\n        gradient: const LinearGradient(\n          colors: [Color(0xFF0f3460), Color(0xFF1a1a2e)],\n        ),\n        borderRadius: BorderRadius.circular(15),\n        boxShadow: [\n          BoxShadow(\n            color: Colors.black.withOpacity(0.1),\n            blurRadius: 8,\n            offset: const Offset(0, 4),\n          ),\n        ],\n      ),\n      child: Material(\n        color: Colors.transparent,\n        child: InkWell(\n          borderRadius: BorderRadius.circular(15),\n          onTap: () {},\n          child: Column(\n            mainAxisAlignment: MainAxisAlignment.center,\n            children: [\n              Icon(icon, color: Colors.white, size: 32),\n              const SizedBox(height: 8),\n              Text(\n                label,\n                style: const TextStyle(\n                  color: Colors.white,\n                  fontWeight: FontWeight.w600,\n                ),\n              ),\n            ],\n          ),\n        ),\n      ),\n    );\n  }\n}