import 'package:flutter/material.dart';\nimport '../models/product.dart';\nimport '../widgets/product_card.dart';\n\nclass ProductsScreen extends StatefulWidget {\n  const ProductsScreen({super.key});\n\n  @override\n  State<ProductsScreen> createState() => _ProductsScreenState();\n}\n\nclass _ProductsScreenState extends State<ProductsScreen> {\n  String _selectedFilter = 'All';\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text(\n          'All Products',\n          style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold),\n        ),\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.filter_list, color: Colors.white),\n            onPressed: () {\n              _showFilterDialog();\n            },\n          ),\n        ],\n      ),\n      body: Column(\n        children: [\n          // Filter Chips\n          Container(\n            height: 60,\n            padding: const EdgeInsets.symmetric(vertical: 8),\n            child: ListView(\n              scrollDirection: Axis.horizontal,\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              children: [\n                _buildFilterChip('All'),\n                _buildFilterChip('Electronics'),\n                _buildFilterChip('Fashion'),\n                _buildFilterChip('Home'),\n                _buildFilterChip('Beauty'),\n                _buildFilterChip('Sports'),\n              ],\n            ),\n          ),\n          // Products Grid\n          Expanded(\n            child: GridView.builder(\n              padding: const EdgeInsets.all(16),\n              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(\n                crossAxisCount: 2,\n                childAspectRatio: 0.7,\n                crossAxisSpacing: 12,\n                mainAxisSpacing: 12,\n              ),\n              itemCount: Product.getAllProducts().length,\n              itemBuilder: (context, index) {\n                final product = Product.getAllProducts()[index];\n                return ProductCard(product: product);\n              },\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildFilterChip(String label) {\n    final isSelected = _selectedFilter == label;\n    return Padding(\n      padding: const EdgeInsets.only(right: 8),\n      child: FilterChip(\n        label: Text(label),\n        selected: isSelected,\n        onSelected: (selected) {\n          setState(() {\n            _selectedFilter = label;\n          });\n        },\n        backgroundColor: Colors.white,\n        selectedColor: const Color(0xFFe94560),\n        labelStyle: TextStyle(\n          color: isSelected ? Colors.white : const Color(0xFF1a1a2e),\n          fontWeight: FontWeight.w600,\n        ),\n        checkmarkColor: Colors.white,\n      ),\n    );\n  }\n\n  void _showFilterDialog() {\n    showModalBottomSheet(\n      context: context,\n      shape: const RoundedRectangleBorder(\n        borderRadius: BorderRadius.vertical(top: Radius.circular(20)),\n      ),\n      builder: (context) {\n        return Container(\n          padding: const EdgeInsets.all(20),\n          child: Column(\n            mainAxisSize: MainAxisSize.min,\n            crossAxisAlignment: CrossAxisAlignment.start,\n            children: [\n              const Text(\n                'Filter Products',\n                style: TextStyle(\n                  fontSize: 20,\n                  fontWeight: FontWeight.bold,\n                ),\n              ),\n              const SizedBox(height: 20),\n              ListTile(\n                leading: const Icon(Icons.sort),\n                title: const Text('Price: Low to High'),\n                onTap: () => Navigator.pop(context),\n              ),\n              ListTile(\n                leading: const Icon(Icons.sort),\n                title: const Text('Price: High to Low'),\n                onTap: () => Navigator.pop(context),\n              ),\n              ListTile(\n                leading: const Icon(Icons.star),\n                title: const Text('Top Rated'),\n                onTap: () => Navigator.pop(context),\n              ),\n              ListTile(\n                leading: const Icon(Icons.new_releases),\n                title: const Text('New Arrivals'),\n                onTap: () => Navigator.pop(context),\n              ),\n            ],\n          ),\n        );\n      },\n    );\n  }\n}