import 'package:flutter/material.dart';\n\nclass ProfileScreen extends StatelessWidget {\n  const ProfileScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text(\n          'Profile',\n          style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold),\n        ),\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.settings, color: Colors.white),\n            onPressed: () {},\n          ),\n        ],\n      ),\n      body: ListView(\n        padding: const EdgeInsets.all(16),\n        children: [\n          // Profile Header\n          Container(\n            padding: const EdgeInsets.all(20),\n            decoration: BoxDecoration(\n              gradient: const LinearGradient(\n                colors: [Color(0xFF0f3460), Color(0xFF1a1a2e)],\n              ),\n              borderRadius: BorderRadius.circular(20),\n              boxShadow: [\n                BoxShadow(\n                  color: Colors.black.withOpacity(0.1),\n                  blurRadius: 10,\n                  offset: const Offset(0, 5),\n                ),\n              ],\n            ),\n            child: Row(\n              children: [\n                Container(\n                  width: 80,\n                  height: 80,\n                  decoration: BoxDecoration(\n                    color: Colors.white,\n                    shape: BoxShape.circle,\n                    border: Border.all(color: const Color(0xFFe94560), width: 3),\n                  ),\n                  child: const Icon(\n                    Icons.person,\n                    size: 50,\n                    color: Color(0xFF0f3460),\n                  ),\n                ),\n                const SizedBox(width: 16),\n                const Expanded(\n                  child: Column(\n                    crossAxisAlignment: CrossAxisAlignment.start,\n                    children: [\n                      Text(\n                        'John Doe',\n                        style: TextStyle(\n                          fontSize: 22,\n                          fontWeight: FontWeight.bold,\n                          color: Colors.white,\n                        ),\n                      ),\n                      SizedBox(height: 4),\n                      Text(\n                        'john.doe@example.com',\n                        style: TextStyle(\n                          fontSize: 14,\n                          color: Colors.white70,\n                        ),\n                      ),\n                      SizedBox(height: 8),\n                      Row(\n                        children: [\n                          Icon(Icons.star, color: Color(0xFFe94560), size: 16),\n                          SizedBox(width: 4),\n                          Text(\n                            'Premium Member',\n                            style: TextStyle(\n                              fontSize: 12,\n                              color: Color(0xFFe94560),\n                              fontWeight: FontWeight.w600,\n                            ),\n                          ),\n                        ],\n                      ),\n                    ],\n                  ),\n                ),\n              ],\n            ),\n          ),\n          const SizedBox(height: 24),\n          \n          // Quick Stats\n          Row(\n            children: [\n              Expanded(\n                child: _buildStatCard('Orders', '24', Icons.shopping_bag, const Color(0xFF0f3460)),\n              ),\n              const SizedBox(width: 12),\n              Expanded(\n                child: _buildStatCard('Wishlist', '12', Icons.favorite, const Color(0xFFe94560)),\n              ),\n              const SizedBox(width: 12),\n              Expanded(\n                child: _buildStatCard('Reviews', '8', Icons.star, const Color(0xFF16213e)),\n              ),\n            ],\n          ),\n          const SizedBox(height: 24),\n          \n          // Menu Items\n          const Text(\n            'Account Settings',\n            style: TextStyle(\n              fontSize: 18,\n              fontWeight: FontWeight.bold,\n              color: Color(0xFF1a1a2e),\n            ),\n          ),\n          const SizedBox(height: 12),\n          _buildMenuItem(Icons.person_outline, 'Edit Profile', () {}),\n          _buildMenuItem(Icons.location_on_outlined, 'Shipping Address', () {}),\n          _buildMenuItem(Icons.payment, 'Payment Methods', () {}),\n          _buildMenuItem(Icons.history, 'Order History', () {}),\n          \n          const SizedBox(height: 24),\n          const Text(\n            'Support',\n            style: TextStyle(\n              fontSize: 18,\n              fontWeight: FontWeight.bold,\n              color: Color(0xFF1a1a2e),\n            ),\n          ),\n          const SizedBox(height: 12),\n          _buildMenuItem(Icons.help_outline, 'Help Center', () {}),\n          _buildMenuItem(Icons.info_outline, 'About Us', () {}),\n          _buildMenuItem(Icons.privacy_tip_outlined, 'Privacy Policy', () {}),\n          \n          const SizedBox(height: 24),\n          SizedBox(\n            width: double.infinity,\n            height: 50,\n            child: OutlinedButton.icon(\n              onPressed: () {},\n              icon: const Icon(Icons.logout),\n              label: const Text(\n                'Logout',\n                style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold),\n              ),\n              style: OutlinedButton.styleFrom(\n                foregroundColor: const Color(0xFFe94560),\n                side: const BorderSide(color: Color(0xFFe94560), width: 2),\n                shape: RoundedRectangleBorder(\n                  borderRadius: BorderRadius.circular(15),\n                ),\n              ),\n            ),\n          ),\n          const SizedBox(height: 20),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildStatCard(String label, String value, IconData icon, Color color) {\n    return Container(\n      padding: const EdgeInsets.all(16),\n      decoration: BoxDecoration(\n        color: Colors.white,\n        borderRadius: BorderRadius.circular(15),\n        boxShadow: [\n          BoxShadow(\n            color: Colors.black.withOpacity(0.05),\n            blurRadius: 10,\n            offset: const Offset(0, 5),\n          ),\n        ],\n      ),\n      child: Column(\n        children: [\n          Icon(icon, color: color, size: 30),\n          const SizedBox(height: 8),\n          Text(\n            value,\n            style: TextStyle(\n              fontSize: 20,\n              fontWeight: FontWeight.bold,\n              color: color,\n            ),\n          ),\n          const SizedBox(height: 4),\n          Text(\n            label,\n            style: TextStyle(\n              fontSize: 12,\n              color: Colors.grey[600],\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildMenuItem(IconData icon, String title, VoidCallback onTap) {\n    return Container(\n      margin: const EdgeInsets.only(bottom: 8),\n      decoration: BoxDecoration(\n        color: Colors.white,\n        borderRadius: BorderRadius.circular(12),\n        boxShadow: [\n          BoxShadow(\n            color: Colors.black.withOpacity(0.03),\n            blurRadius: 5,\n            offset: const Offset(0, 2),\n          ),\n        ],\n      ),\n      child: ListTile(\n        leading: Icon(icon, color: const Color(0xFF0f3460)),\n        title: Text(\n          title,\n          style: const TextStyle(\n            fontSize: 16,\n            fontWeight: FontWeight.w500,\n          ),\n        ),\n        trailing: const Icon(Icons.arrow_forward_ios, size: 16, color: Colors.grey),\n        onTap: onTap,\n        shape: RoundedRectangleBorder(\n          borderRadius: BorderRadius.circular(12),\n        ),\n      ),\n    );\n  }\n}